import React from 'react';
import { Paper, Typography, Avatar, Box } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import styles from './Testimonials.module.scss'; // Estilo do carrossel
import AvatarImage from '../../assets/img/avatar.jpg';

// Dados fictícios de depoimentos
const testimonials = [
  {
    name: 'Jair',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  {
    name: 'Diego',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  {
    name: 'Jair',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  // Adicione mais depoimentos conforme necessário
];

const TestimonialsCarousel = () => {
  return (
    <Paper sx={{ backgroundColor: '#fff', padding: '20px' }}> {/* Definindo um fundo branco */}
      <Carousel
        className={styles.carousel}
        showArrows={true}
        showThumbs={false}
        showIndicators={true} // Exibir indicadores de navegação
        showStatus={false} // Ocultar status do carrossel
        infiniteLoop={true} // Habilitar loop infinito
        autoPlay={true} // Iniciar reprodução automática
        interval={4000} // Intervalo entre os slides (em milissegundos)
      >
        {testimonials.map((testimonial, index) => (
          <Box key={index} className={styles.slide}>
            <Box className={styles.content}>
              <Typography variant="body1" className={styles.quote}>
                {testimonial.quote}
              </Typography>
              <Box display="flex" alignItems="center">
                <Typography variant="subtitle1" className={styles.author}>
                  <span className={styles.name}>{testimonial.name}</span> -{' '}
                  {testimonial.role}
                </Typography>
              </Box>
            </Box>
          </Box>
        ))}
      </Carousel>
    </Paper>
  );
};

export default TestimonialsCarousel;
