import React from 'react';
import { Paper, Box, Typography, Avatar } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import AvatarImage from '../../assets/img/avatar.jpg';

interface Testimonial {
  rating: number;
  text: string;
  author: string;
  role: string;
}

const testimonials: Testimonial[] = [
  {
    rating: 5,
    text:
      "Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.",
    author: "Jair",
    role: "Compras",
  },
  {
    rating: 5,
    text:
      "Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.",
    author: "Diego",
    role: "Compras",
  },
  // Adicione mais depoimentos conforme necessário
];

const TestimonialsCarousel = () => {
  return (
    <Paper sx={{ backgroundColor: '#fff', padding: '20px' }}>
      <Carousel
        showArrows={true}
        showThumbs={false}
        showIndicators={true}
        showStatus={false}
        infiniteLoop={true}
        autoPlay={true}
        interval={4000}
      >
        {testimonials.map((testimonial, index) => (
          <Box key={index} sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <TestimonialComponent {...testimonial} />
          </Box>
        ))}
      </Carousel>
    </Paper>
  );
};

const TestimonialComponent: React.FC<Testimonial> = ({ rating, text, author, role }) => {
  return (
    <Box sx={{ textAlign: 'center' }}>
      <Typography variant="body1" sx={{ marginBottom: 1 }}>
        "{text}"
      </Typography>
      <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
        {author} - {role}
      </Typography>
      <Typography variant="subtitle2" sx={{ color: '#888' }}>
        {rating} estrelas
      </Typography>
    </Box>
  );
};

export default TestimonialsCarousel;
