import React, { useState, ChangeEvent, FormEvent } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import CardMedia from '@mui/material/CardMedia';
import ContactImage from '../../assets/img/contact.jpeg';

const ContactForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        cpfCnpj: '',
        product: ''
    });
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        try {
            const response = await fetch('/enviar-email', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                setSuccessMessage('Email enviado com sucesso!');
                setErrorMessage('');
                setFormData({
                    name: '',
                    email: '',
                    cpfCnpj: '',
                    product: ''
                });
            } else {
                setSuccessMessage('');
                setErrorMessage('Erro ao enviar email');
            }
        } catch (error) {
            console.error('Erro ao enviar email:', error);
            setSuccessMessage('');
            setErrorMessage('Erro ao enviar email');
        }
    };

    return (
        <Container maxWidth="md">
            <Box sx={{ mt: 8, mb: 8 }}>
                <Typography variant="h4" fontSize={30} fontWeight={600} fontFamily={"Montserrat"} align="center" sx={{ mb: 4 }}>Faça uma cotação</Typography>
                <Grid container spacing={3}>
                    <Grid item xs={12} sm={12} md={6}>
                        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column' }}>
                            <TextField
                                label="Nome"
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                                sx={{ mb: 2 }}
                            />
                            <TextField
                                label="Email"
                                id="email"
                                name="email"
                                type="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                sx={{ mb: 2 }}
                            />
                            <TextField
                                label="CPF/CNPJ"
                                id="cpfCnpj"
                                name="cpfCnpj"
                                value={formData.cpfCnpj}
                                onChange={handleChange}
                                required
                                sx={{ mb: 2 }}
                            />
                            <TextField
                                label="Produto"
                                id="product"
                                name="product"
                                value={formData.product}
                                onChange={handleChange}
                                required
                                sx={{ mb: 2 }}
                            />
                            <Button variant="contained" type="submit">Enviar</Button>
                            {successMessage && <Typography variant="body1" color="success">{successMessage}</Typography>}
                            {errorMessage && <Typography variant="body1" color="error">{errorMessage}</Typography>}
                        </Box>
                    </Grid>
                    <Grid item xs={12} sm={6} md={6} sx={{ display: 'flex', justifyContent: 'center' }}>
                        <CardMedia
                            component="img"
                            image={ContactImage}
                            alt="Contact"
                            sx={{ width: '100%', height: 'auto' }}
                        />
                    </Grid>
                </Grid>
            </Box>
        </Container>
    );
};

export default ContactForm;
