import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDownLeftAndUpRightToCenter } from '@fortawesome/free-solid-svg-icons';

const backgroundVideo = require('src/assets/videos/video1.mp4').default;

const Hero = () => {
  return (
    <Box sx={{
      position: 'relative',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      overflow: 'hidden',
    }}>
      <video
        autoPlay
        loop
        muted
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          zIndex: -1,
        }}
      >
        <source src={backgroundVideo} type="video/mp4" />
      </video>
      <Box sx={{
        zIndex: 1,
        textAlign: 'center',
        color: 'black',
      }}>
        <Typography variant="h2" sx={{ marginBottom: '1rem' }}>Serras para alto desempenho</Typography>
        <Typography variant="body1" sx={{ marginBottom: '2rem' }}>Conheça nossos produtos e serviços de alta qualidade.</Typography>
        <Box sx={{ display: 'flex', justifyContent: 'center', marginBottom: '2rem' }}>
          <Button variant="contained" sx={{ marginRight: '1rem' }}>Nossos produtos <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} style={{ marginLeft: '0.5rem' }} /></Button>
          <Button variant="contained">Fale conosco <FontAwesomeIcon icon={faDownLeftAndUpRightToCenter} style={{ marginLeft: '0.5rem' }} /></Button>
        </Box>
      </Box>
    </Box>
  );
};

export default Hero;
