  },
  // Adicione mais depoimentos conforme necessário
];

const TestimonialsCarousel = () => {
  return (
    <Box padding={10}>
      <Typography variant="h2" fontSize={25} align="center" mb={3}>O que dizem os nossos clientes</Typography>

      <Carousel
        showArrows={true}
        showThumbs={false}
        showIndicators={true}
        showStatus={false}
        infiniteLoop={true}
        autoPlay={true}
        interval={4000}
      >
        {testimonials.map((testimonial, index) => (
          <Box key={index} display="flex" justifyContent="center" alignItems="center">
            <Box ml={2}>
              <Typography variant="body1" align="center" mb={2}>{testimonial.quote}</Typography>
              <Typography variant="subtitle1" align="center">{testimonial.name} - {testimonial.role}</Typography>
            </Box>
          </Box>
        ))}
      </Carousel>
    </Box>
  );
};

export default TestimonialsCarousel;
