import React from 'react';
import { Box, Typography, Grid } from '@mui/material';
import CompanyBackground from '../../assets/background/company-background.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';



const Services = () => {

  return (
    <Box id="services" className={classes.services}> {/* Adicionado um id para o componente Services */}
      <Typography variant="h2" fontSize={25} marginBottom={2} className={classes.servicesTitle}>
        <FontAwesomeIcon style={{ marginRight: '1rem', color: '#D39E00' }} icon={faArrowRight} />
        Nossos Serviços
      </Typography>
      <Typography variant="body1" fontSize={15}  marginBottom={10} className={classes.servicesSubTitle}>
        Somos especialistas em serra circular, máquinas de serra fita, laminas de serra fita, máquinas de serra circular e consultoria técnica. Oferecemos produtos de extrema qualidade e preços altamente competitivos com o intuito de promover o melhor desempenho na operação e custo beneficio do cliente
      </Typography>
      <Grid container spacing={0}>
        {Array.from({ length: 6 }, (_, index) => (
          <Grid item xs={12} sm={6} md={4} key={index} className={classes.serviceContainer}> {/* Alterado para 4 colunas em telas pequenas e 3 em médias */}
            <Box className={classes.serviceCard}>
              <Box className={classes.img} />
              <Typography variant="h3" fontSize={13} className={classes.serviceTitle}>PRODUTOS</Typography>
              <Typography variant="body1" fontSize={13} className={classes.serviceDescription}>Extrema qualidade</Typography>
              <Typography variant="body1" fontSize={13} className={classes.serviceInfo}>Fornecedores rigorosamente avaliados para oferecer os melhores itens para o corte.</Typography>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Services;
