import React from 'react';
import { Paper, Typography, Avatar, Box } from '@mui/material';
import { Rating } from '@mui/material';
import AvatarImage from '../../assets/img/avatar.jpg';

const Testimonial = ({ rating, text, author, role }) => {
  return (
    <Paper
      sx={{
        backgroundColor: '#fff',
        padding: '20px',
        '& .content': {
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          textAlign: 'center',
          '& .quote': {
            marginBottom: '15px',
          },
          '& .author': {
            marginTop: '15px',
            fontWeight: 'bold',
          },
        },
      }}
    >
      <Box className="content">
        <Rating name="testimonial-rating" value={rating} precision={0.5} readOnly />
        <Typography variant="body1" className="quote">
          {text}
        </Typography>
        <Box display="flex" alignItems="center" justifyContent="center">
          <Avatar src={AvatarImage} alt="Avatar" />
          <Typography variant="subtitle1" className="author">
            {author} - {role}
          </Typography>
        </Box>
      </Box>
    </Paper>
  );
};

export default Testimonial;
