import React from "react";
import { useParams, Link } from "react-router-dom";
import Button from "@mui/material/Button";

const ProductPage: React.FC = () => {
  const { productId } = useParams();

  // Simulação de busca dos detalhes do produto com base no ID fornecido nos parâmetros da rota
  // Aqui você pode substituir esta simulação pela lógica real para buscar os detalhes do produto

  const productDetails = product.find((product: any) => product.id === Number(productId));

  return (
    <div>
      {productDetails ? (
        <div>
          <h2>{productDetails.title}</h2>
          <p>ID do Produto: {productDetails.id}</p>
          <ul>
            {productDetails.topics.map((topic, index) => (
              <li key={index}>{topic}</li>
            ))}
          </ul>
          <Link to="/">
            <Button variant="contained" color="primary">
              Voltar
            </Button>
          </Link>
        </div>
      ) : (
        <p>Produto não encontrado</p>
      )}
    </div>
  );
};

export default ProductPage;
