import React from 'react';
import { Box, Typography, Grid, Card, CardContent } from '@mui/material';
import CompanyBackground from '../../assets/background/company-background.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

const Services = () => {
  return (
    <Box id="services" sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', textAlign: 'center', minHeight: '100vh', backgroundColor: '#f7f7f7', paddingBottom: '15rem', paddingTop: '5rem' }}>
      <Typography  variant="h2" fontSize={25} fontWeight={400} fontFamily="Montserrat, sans-serif"marginBottom={2} sx={{ display: 'flex', alignItems: 'center', color: '#D39E00' }}>
        <FontAwesomeIcon style={{ marginRight: '1rem' }} icon={faArrowRight} />
        Nossos Serviços
      </Typography>
      <Typography variant="body1" fontSize={15} fontFamily="Montserrat, sans-serif" marginBottom={10} sx={{ maxWidth: '600px', color: '#505050', margin: '0 auto' }}>
        Somos especialistas em serra circular, máquinas de serra fita, laminas de serra fita, máquinas de serra circular e consultoria técnica. Oferecemos produtos de extrema qualidade e preços altamente competitivos com o intuito de promover o melhor desempenho na operação e custo benefício do cliente
      </Typography>
      <Grid container spacing={3} marginTop={5} >
        {Array.from({ length: 6 }, (_, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card sx={{ width: '100%', marginBottom: '20px', backgroundColor: '#F2BF27' }}>
              <CardContent>
                <Box sx={{ height: '100px', width: '100%', backgroundImage: `url(${CompanyBackground})`, backgroundSize: 'cover', backgroundPosition: 'center', marginBottom: '20px' }} />
                <Typography variant="h3" fontSize={13} fontFamily="Montserrat, sans-serif"sx={{ marginBottom: '10px', color: '#fff' }}>PRODUTOS</Typography>
                <Typography variant="body1" fontSize={13} fontFamily="Montserrat, sans-serif"sx={{ marginBottom: '10px', color: '#fff' }}>Extrema qualidade</Typography>
                <Typography variant="body1" fontSize={13} fontFamily="Montserrat, sans-serif"sx={{ color: '#666' }}>Fornecedores rigorosamente avaliados para oferecer os melhores itens para o corte.</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Services;
