// Header.js
import React, { useState } from 'react';
import styles from './Header.module.scss';
import Logo from '../../assets/logo/hiperion-logo.svg';

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const navItems = ['Inicio', 'Sobre', 'Serviços', 'Parceiros e Clientes', 'Produtos', 'Testemunhas', 'Blog', 'Contato'];

  return (
    <header className={styles.header}>
      <img className={styles.logo} src={'https://lirp.cdn-website.com/9235761d/dms3rep/multi/opt/C-C3-B3pia-de-Sem-nome-4198ecf5-cd7c09d2-81c8314f-512w.png'} alt="Logo" />
      <div className={styles.hamburger} onClick={toggleMenu}>
        <div className={styles.line}></div>
        <div className={styles.line}></div>
        <div className={styles.line}></div>
      </div>
      <nav className={`${styles.nav} ${menuOpen ? '' : styles.closed}`}> {/* Adicionei uma classe condicional para controlar a abertura e fechamento do menu */}
        <ul>
          {navItems.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
