import React from 'react';
import { Box, Typography, Link } from '@mui/material';
import { styled } from '@mui/system';

const Footer = () => {
  const socialLinks = [
    { name: 'Facebook', url: 'https://www.facebook.com/' },
    { name: 'Twitter', url: 'https://twitter.com/' },
    { name: 'Instagram', url: 'https://www.instagram.com/' },
  ];

  const footerColumns = [
    {
      title: 'Location',
      content: [
        '123 Street Name',
        'City, Country',
      ],
    },
    {
      title: 'Contact',
      content: [
        'info@example.com',
        '+123-456-7890',
      ],
    },
    {
      title: 'Services',
      content: [
        'Service 1',
        'Service 2',
        'Service 3',
      ],
    },
  ];

  const StyledFooter = styled(Box)({
    backgroundColor: '#222',
    padding: '2rem 0',
    width: '100%',
  });

  const StyledFooterContent = styled(Box)({
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '2rem',
    borderTop: '1px solid #6c757d',
    borderBottom: '1px solid #6c757d',
  });

  const StyledFooterColumn = styled(Box)({
    flex: 1,
    marginRight: '2rem',

    '& p': {
      color: '#fff',
    },
  });

  const StyledFooterHeading = styled(Typography)({
    fontWeight: 'bold',
    color: '#fff',
    fontSize: '1.2rem',
  });

  const StyledFooterLink = styled(Link)({
    color: '#fff',
    textDecoration: 'none',

    '&:hover': {
      textDecoration: 'underline',
    },
  });

  return (
    <StyledFooter component="footer">
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', color: '#fff' }}>
        <Typography variant="body1">Â© 2024 Hiperion Serras All Rights Reserved.</Typography>
        <Box>
          <Typography variant="body1" className="socialText">Follow us on:</Typography>
          <Box className="socialLinks">
            {socialLinks.map(link => (
              <Link key={link.name} href={link.url} className="socialLink">
                {link.name}
              </Link>
            ))}
          </Box>
        </Box>
      </Box>
      <StyledFooterContent>
        {footerColumns.map(column => (
          <StyledFooterColumn key={column.title}>
            <StyledFooterHeading>{column.title}</StyledFooterHeading>
            {column.content.map((item, index) => (
              <Typography key={index} variant="body2" paragraph>
                {item}
              </Typography>
            ))}
          </StyledFooterColumn>
        ))}
      </StyledFooterContent>
      <Box sx={{ textAlign: 'center', marginTop: '2rem', fontSize: '0.8rem', color: '#ffff' }}>
        <Typography variant="body2" className="copyRight">Diego Websites Development</Typography>
      </Box>
    </StyledFooter>
  );
};

export default Footer;
