import React from 'react';
import { Paper, Typography, Avatar, Box } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import AvatarImage from '../../assets/img/avatar.jpg';

// Dados fictícios de depoimentos
const testimonials = [
  {
    name: 'Jair',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  {
    name: 'Diego',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  {
    name: 'Jair',
    role: 'Compras',
    avatar: AvatarImage,
    quote:
      'Eu só tenho elogios para o atendimento que recebi e para o serviço prestado pela sua empresa. A utilização da lâmina de serra fita e o alto rendimento proporcionaram um trabalho preciso e rápido. Sem dúvidas, recomendo a todos os meus amigos e colegas de trabalho.',
  },
  // Adicione mais depoimentos conforme necessário
];

const TestimonialsCarousel = () => {
  return (
    <Paper
      sx={{
        backgroundColor: '#fff',
        padding: '20px',
        '& .carousel': {
          '& .slide': {
            '& .content': {
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center',
              '& .quote': {
                marginBottom: '15px',
              },
              '& .author': {
                fontWeight: 'bold',
              },
            },
          },
        },
      }}
    >
      <Carousel
        className="carousel"
        showArrows={true}
        showThumbs={false}
        showIndicators={true} // Exibir indicadores de navegação
        showStatus={false} // Ocultar status do carrossel
        infiniteLoop={true} // Habilitar loop infinito
        autoPlay={true} // Iniciar reprodução automática
        interval={4000} // Intervalo entre os slides (em milissegundos)
      >
        {testimonials.map((testimonial, index) => (
          <Box key={index} className="slide">
            <Box className="content">
              <Typography variant="body1" className="quote">
                {testimonial.quote}
              </Typography>
              <Box display="flex" alignItems="center">
                <Typography variant="subtitle1" className="author">
                  <span>{testimonial.name}</span> - {testimonial.role}
                </Typography>
              </Box>
            </Box>
          </Box>
        ))}
      </Carousel>
    </Paper>
  );
};

export default TestimonialsCarousel;
