import React from 'react';
import { CardMedia, Typography, Button } from '@mui/material';
import styles from './Hero.module.scss';
import video from '../../assets/videos/video1.mp4'; // Importe o vídeo

const Hero = () => {
  return (
    <div className={styles.hero}>
      {/* Vídeo de fundo usando CardMedia */}
      <CardMedia
        component="video" // Define o componente como vídeo
        autoPlay // Reprodução automática
        loop // Reprodução em loop
        playsInline // Reprodução em linha
        src={video} // URL do vídeo
        className={styles.videoBackground} // Estilos CSS adicionais
      />
      {/* Conteúdo sobreposto */}
      <div className={styles.content}>
        <Typography variant="h1" component="h1">
          Serras para alto desempenho
        </Typography>
        <Typography variant="body1" component="p">
          Conheça nossos produtos e serviços de alta qualidade.
        </Typography>
        <div className={styles.buttonContainer}>
          <Button variant="contained" color="primary">
            Nossos produtos
          </Button>
          <Button variant="outlined" color="primary">
            Fale conosco
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Hero;
